mapper.update:
UPDATE CHATREAD
SET CHATREAD_DATE = SYSDATE()
WHERE CHATROOM_ID = #{chatroom_id}
AND MEM_ID = #{mem_id}

mapper.select:
SELECT MEM_ID, CHATREAD_DATE
FROM CHATREAD
WHERE CHATROOM_ID = #{chatroom_id}

SELECT CHATROOM_ID, CHATREAD_DATE
FROM CHATREAD
WHERE MEM_ID = #{mem_id}

SELECT CHATREAD_DATE
FROM CHATREAD
WHERE MEM_ID = #{mem_id}
ORDER BY CHATREAD_DATE DESC LIMIT 1;

mapper.delete:
DELETE FROM CHATREAD
WHERE CHATROOM_ID = #{chatroom_id}
AND MEM_ID = #{mem_id}
